import React, {useState, useEffect} from 'react';
import axios from 'axios';
import { Map, GoogleApiWrapper, Marker } from 'google-maps-react'
import Moment from 'react-moment'
import numeral from 'numeral'
import './Sidebar.css'
import './Menubar.css'

import image from '../Image/apartment_.png'
import cancel from '../Image/cancel.png'
import like1 from '../Image/like.png'
import like2 from '../Image/like-toggle.png'
import star1 from '../Image/star1.PNG'
import star2 from '../Image/star2.PNG'
import room4 from '../Image/room4.PNG'

import SearchBar from './SearchBar'
import HappyChart from './Chart';

export const MapMarkers = (props) => { 
    const [item, setItem] = useState([]);
    const [houseDetail, setHouseDetail] = useState();
    const [center, setCenter] = useState()
    const [zoom, setZoom] = useState()

    useEffect(() => {
      setCenter({lat: 37.5, lng: 127})
      setZoom(15)
      loadAsyncData();
    }, [])

    const loadAsyncData = () => {
        let url = `/happyhouse/houseInfos`;

        axios.get(url).then(({data}) => {
            data = data.houseInfoList
            setItem(data);
        })
    }

    const displayMarkers = () => {
      return item.map((data) => (
          <Marker className='marker-image' 
          key={data._id} 
          position={{lat:data.lat, lng:data.lng}}
          icon={{
            url: image,
            scaledSize: new props.google.maps.Size(40,40),
            //labelOrigin: new props.google.maps.Size(50, 115),
          }}
          label={{
            text: `${numeral(data.houseHoldNum).format('0,0')}세대`,
            fontSize: "13px",
            fontFamily: "Do Hyeon",
            color: "white",
            className: 'label'
          }}
          onClick = {()=>{sidebarToggle();
          setHouseDetail(data)}}
          />
          ))
  }

    const mapStyles = {
        width: '100%',
        height: '100%',
    };

    const sidebarToggle = () => {
      var con = document.getElementById("sideBar");
      if(con.style.display==='block'){
        con.style.display='none';
      }else{
        con.style.display='block';
      }
    }

    return(
    <div>
    <React.Fragment>
        <Map google={props.google} zoom={zoom} style={mapStyles} center={center}>
            {displayMarkers()}
          <SearchBar setCenter={setCenter} setZoom={setZoom} />
        </Map>

        <div className="side-bar-wrap" id = "sideBar" >
          {console.log(houseDetail)}
        </div>
    </React.Fragment></div>
    );
}

export default GoogleApiWrapper({
    apiKey: "AIzaSyCvljxDf0P6qv5kvTo5WIYRlWU3VZn3hks"
})(MapMarkers);
