import React, { useState } from "react";
import Select from 'react-select'
import './css/SearchBar.css'

const SearchDanjiBar = (props) => {
    const [options, setDate] = useState([
        { value: "서울특별시 종로구", label: "서울특별시 종로구", state:{lng:"126.9989704	 ", lat: "37.57067532" }}
    ])
    const [choice, setChoice] = useState(); 


    useEffect(() => {
        loadAsyncData();
      }, [])
  
    const loadAsyncData = () => {
          let url = `https://joj5opq81m.execute-api.us-east-2.amazonaws.com/happyhouse/houseinfos/address`;
  
          axios.get(url).then(({data}) => {
              data = data.houseInfoList
              setDate({value : data.address , label : data.address , state:{lng:data.lng, lat:data.lat}});
          })
      }

    const onChange = (state) => {
        props.setCenter(state)
        props.setZoom(15)
    }

    const customStyles = {
        option: (provided, state) => ({
          ...provided,
          borderBottom: '1px solid lightgray',
          color: 'rgb(51, 51, 51)',
          padding: 20,
          ":hover": {
            // Overwrittes the different states of border
            backgroundColor: 'lightgray',
          },
          fontFamily: 'Do Hyeon',
        }),
      }

    return(
        <div className = "search-bar">
                <Select
                options={options} 
                value={options.find(op => {return op.value === choice })} 
                placeholder={<div className="select-placeholder-text">주소 검색</div>}
                theme={(theme) => ({
                    ...theme,
                    borderRadius: '5px',
                    colors: {
                    ...theme.colors,
                      primary25: 'lightgray',
                      primary: 'lightgray',
                    },
                  })}
                styles={customStyles}
                onChange={(value) => { onChange(value.state); }}/>
        </div>
    )

};

export default SearchDanjiBar;
