import React, {useEffect, useState} from 'react'; //https://github.com/imtaekh/my_app/blob/4485452eee5a48e94d2ee0b7892ef0181ba4302c/models/User.js
import $ from 'jquery';
import {
    Button,
    Col,
    Container,
    Form,
    FormGroup,
    Input,
    InputGroup,
    InputGroupAddon,
    InputGroupText,
    Modal,
    ModalHeader,
    Row,
    Table
} from 'reactstrap';
import axios from 'axios';
import './community.css'
import Moment from 'react-moment'

function ReplyCommunity(props) {

    const [_id, set_id] = useState(props._id);
    const [userId, setUserId] = useState(props);
    const [writeDate, setWriteDate] = useState();
    const [title, setTitle] = useState();
    const [content, setContent] = useState();
    const [activityHistoryList, setActivityHistoryList] = useState();

    const handleChangeUserId = (e) => {
        e.preventDefault();
        setUserId(e.target.value);
    }
    const handleChangeWriteDate = (e) => {
        e.preventDefault();
        setWriteDate(e.target.value);
    }
    const handleChangeTitle = (e) => {
        e.preventDefault();
        setTitle(e.target.value);
    }
    const handleChangeContent = (e) => {
        e.preventDefault();
        setContent(e.target.value);
    }

    const replyList = (community) => (

        <li className="li" key={community._id} id={community._id}>

            <div class="community-block" id="second">
                <td className="id">{community._id}</td>
                <td className="id">{community.writeDate}</td>
                <td id="header" className={"readCommunity"}>
                    <h4>[{community.title}]</h4>
                </td>

                <div>
                    <table class="houseInfo">
                        <tr>
                            <td id="a">작성자</td>
                            <td>{community.userId}</td>
                            <td id="a">작성일</td>
                            <td>
                                <Moment format="YY.MM.DD">{community.writeDate}</Moment>
                            </td>
                        </tr>
                    </table>
                </div>
                {community.content}
                {/* <div class="community-content">
                    {community.content}
                </div> */}
            </div>
        </li>
    );
    const handReply = () => {  //댓글등록
        let newDate = new Date();
        var form={
            title : "[댓글]",
            content : content,
            userId : localStorage.getItem("userID"),
            groupId : localStorage.getItem("groupId"),
            replyOrder: 0,
            replyStep: 1,
            writeDate: newDate,
            numberOfView: 0
        };
        axios.post('/happyhouse/communities', form).then((res) => {
            alert("게시글이 작성 완료")
            window.location.reload();
            props.toggle()
        }).catch(function (error){
            console.log(error)
            
        })
    }

    const readCommunity = () => {  //게시글 정보

        var form = {
            _id: localStorage.getItem("community_id")
        };
        axios
            .post('/happyhouse/communities/detail', form)
            .then((res) => {

                console.log(res.data);
                console.log(res.data.communitys.userId);
                setUserId(res.data.communitys.userId);
                setWriteDate(res.data.communitys.writeDate);
                setTitle(res.data.communitys.title);
                setContent(res.data.communitys.content);
            })
            .catch(function (error) {
                console.log(error);
            })
        }
       
        const readReply  = () => {  //댓글 목록 조회

            var form = {
                groupId: localStorage.getItem("groupId")
            };
            axios
                .post('/happyhouse/communities/reply', form)
                .then(({data}) => {
                    data = data.communitysList
                    setActivityHistoryList(data.map(replyList))
    
                })
                .catch(function (error) {
                    console.log(error);
                })
            }
    
    

    useEffect(() => {
    
        readCommunity(); //게시글 상세조회
        readReply();
    }, [])
    return (
       
        <div className="dv">
    
  <div className="createCommunity">
                <div className="community-title">
                    <div id="title">커뮤니티 작성</div>
                </div>

            </div>
            
                <div class="community-block">
                   
                    <td id="header">
                        <h4>
                            <td name="title">{content}</td>
                        </h4>
                    </td>

                     <div class="community-content">
                        <td
                            name="content"
                            type="textarea"
                            Rows="7"
                            >{content}</td>
                    </div>  
                    <br></br>
                   <div className="button-container">
                  <button id="review-upload" onClick = {()=>{window.location.href ='/communities'}} style={{float: 'left'}}>취소</button>
                </div> 
                    
                    <br></br>
                 </div> 
                <h4>comments</h4>
                <br>
                </br>
                <FormGroup>
                    <InputGroup
                        style={{
                            marginTop: "1%",
                            marginBottom: "1%"
                        }}>
                        <InputGroupAddon addonType="prepend">
                            <InputGroupText>내용</InputGroupText>
                        </InputGroupAddon>
                        <Input type="textarea" name="content" id="content"  cols="50" rows="10" onChange={handleChangeContent}/>
                    </InputGroup>
                    
                    </FormGroup>
                    <div className="form-group">
                        <Button color="primary" block="block" onClick = {()=> handReply()}>등록</Button>
                    </div>
                 {activityHistoryList}
           
                 <a class="article">
                     <img src="https://cf-fpi.everytime.kr/0.png" class="picture large"/>
                         <div class="profile">
                             <h3 class="large1">익명</h3>
                             <time class="large">42분 전</time>
                        </div>
                        <ul class="status">
                            <li class="messagesend" data-modal="messageSend" data-article-id="190523097" data-is-anonym="1">쪽지</li><li class="abuse">신고</li>
                        </ul>
                        <h2 class="large">오늘 처음 모의토익 쳐보는데</h2>
                        <p class="large">마킹은 연필루 하나요?? 일반 연필이어도 상관없을까요?.?(・・?)</p>
                        <ul class="status">
                            <li title="공감" class="vote">0</li>
                            <li title="댓글" class="comment">7</li>
                            <li title="스크랩" class="scrap">0</li>
                        </ul>
                        <hr></hr><input type="hidden" name="190523097_comment_anonym" value="0"></input>
                        
                </a>
            {/* <Modal isOpen={modalCreateReply}>
                        <ModalHeader toggle={toggleCreateReply}>댓글 등록</ModalHeader>

                         <CreateReply postId={modalInput}></CreateReply>
            </Modal>
            <Modal isOpen={modalDeleteReply}>
                        <ModalHeader toggle={toggleDeleteReply}>댓글 삭제</ModalHeader>

                         <DeleteReply postId={modalInput}></DeleteReply>
            </Modal> */
            }
            {/* <InputGroup>
                    <InputGroupAddon addonType="prepend">
                        <InputGroupText>댓글</InputGroupText>
                    </InputGroupAddon>
                    <Input
                        type="textarea"
                        name="content"
                        placeholder="댓글"
                        onChange={handleReplyOnChange}
                        value={reply}></Input>
                </InputGroup>
                <Button
                    className="btn btn-primary btn-block w-25"
                    color={"primary"}
                    style={{
                        float: 'right'
                    }}
                    type="post"
                    onClick={createReply}>{"댓글작성"}</Button> */
            }
        </div>

    )
}
export default ReplyCommunity;
