import React, {useEffect, useState} from 'react';
import $ from 'jquery';
import {
    Button,
    Col,
    Container,
    Form,
    FormGroup,
    Input,
    InputGroup,
    InputGroupAddon,
    InputGroupText,
    Modal,
    ModalHeader,
    Row,
    Table
} from 'reactstrap';
import axios from 'axios';
import './Review.css'
function ReadReview(props) {
    const [_id, set_id] = useState(props._id);
    const [userId, setUserId] = useState();
    const [title, setTitle] = useState()
    const [monthlyRentCharge, setMonthlyRentCharge] = useState()
    const [adminCharge, setAdminCharge] = useState() 
    const [region, setRegion] = useState()     
    const [typeName, setTypeName] = useState()
    const [merit, setMerit] = useState()
    const [demerit, setDemerit] = useState()
    const [picture, setPicture] = useState('')
    const [imagePreviewUrl, setImagePreviewUrl] = useState('');
    const [star, setStar] = useState()
    const [writeDate, setWriteDate] = useState()

    const [modalInput, setModalInput] = useState("default");
    const [tableData, setTableData] = useState(); //댓글 목록 조회
    const [isReadOnly, setIsReadOnly] = useState(true); //댓글 수정활성화
    const handlChangeUserId= (e) => {
        e.preventDefault();
        setUserId(e.target.value);
    };  
    const handlChangeTitle = (e) => {
        e.preventDefault();
        setTitle(e.target.value);
    };  
    const handlChangeMonthlyRentCharge = (e) => {
        e.preventDefault();
        setMonthlyRentCharge(e.target.value);
    };   
    const handlChangeAdminCharge = (e) => {
        e.preventDefault();
        setAdminCharge(e.target.value);
    };   
    const handlChangeRegion = (e) => {
        e.preventDefault();
        setRegion(e.target.value);
    }; 
    const handlChangeTypeName = (e) => {
        e.preventDefault();
        setTypeName(e.target.value);
    };     
    const handlChangeMerit = (e) => {
        e.preventDefault();
        setMerit(e.target.value);
    };   
    const handlChangeDemerit = (e) => {
        e.preventDefault();
        setDemerit(e.target.value);
    };     
    const handleChangePicture = (e) => {
        e.preventDefault();

        let reader = new FileReader();
        let file = e.target.files[0];
        reader.onloadend = () => {
            setPicture(file);
            setImagePreviewUrl(reader.result);
        }
        reader.readAsDataURL(file);
    }
    const handlChangeStar = (e) => {
        e.preventDefault();
        setStar(e.target.value);
    }; 
    const handlChangeWriteDate = (e) => {
        e.preventDefault();
        setWriteDate(e.target.value);
    }; 
    const toggleIsReadOnly = () => {
        setIsReadOnly(!isReadOnly);
    }
   
    const readReview = () => {

        var form = {
            _id: _id  //거주후기 id
        };
        axios
            .post('/happyhouse/reviews/:id', form)
            .then((res) => {

                console.log(res.data);
                setUserId(res.data.reviews.userId);
                setTitle(res.data.reviews.userId);
                setMonthlyRentCharge(res.data.reviews.userId);
                setAdminCharge(res.data.reviews.userId);
                setRegion(res.data.reviews.userId);
                setTypeName(res.data.reviews.userId);
                setMerit(res.data.reviews.userId);
                setDemerit(res.data.reviews.userId);
                setPicture(res.data.reviews.picture);
                setStar(res.data.reviews.title);
                setWriteDate(res.data.reviews.writeDate);
            })
            .catch(function (error) {
                console.log(error);
            })
        }

   
    const updateCommunity = () => {
        var form = new FormData;

      
        form.append("title", title);
        form.append("content", content);

        axios
            .post('/community/updatePost', form, {
                headers: {
                    'content-type': 'multipart/form-data'
                }
            })
            .then((response) => {
                window
                    .location
                    .reload();
            })
    }
    const deleteReview = () => {
      
 
        axios
            .delete('/happyhouse/communities/:id',{
                data:{
                    _id: _id
                }
            })
            .then((res) => {
           
                alert("게시글이 삭제 완료")
                window.location.reload();
                props.toggle()
                
            })
            .catch(function (error) {
                console.log(error);
            })
        }

    useEffect(() => {
       // var form = new FormData;
       // form.append("id", localStorage.getItem('id'));
        readReview(); //게시글 상세조회  
    }, [])
    return (
        <div className="container">   

                            <table class="houseInfo">
                                <tr>
                                    <td id="header">제목</td>
                                    <h4>
                                        <Input
                                            name="userId"
                                            onChange={handleTitleOnChange}
                                            value={title}
                                            readOnly={isReadOnly}></Input>
                                    </h4>
                                </tr>
                              
                                <tr>
                                    <td id="header">내용</td>
                                    <Input
                                        name="content"
                                        cols="50"
                                        rows="10"
                                        onChange={handleContentOnChange}
                                        value={content}
                                        readOnly={isReadOnly}></Input>
                                </tr>

                            </table>

                            <div className="button-container">
                            <button id="community-upload" onClick={toggleIsReadOnly}style={{float: 'left'}}>수정</button>
                            <button id="community-upload" onClick={updateCommunity}style={{float: 'left'}}>완료</button>

                            <button id="community-upload" onClick={deleteCommunity}style={{float: 'right'}}>삭제</button>
                            </div>
               
                
               
            </div>
    
    )
}
export default ReadReview;
