import Pagination from '../source/Pagination'
import { paginate } from '../source/paginate';
import React, {useState, useEffect} from 'react';
import axios from 'axios'
import './css/community.css'
import $ from 'jquery';
import {Modal, ModalHeader} from 'reactstrap';
import CreateCommunity from './CreateCommunity'
import ReadCommunity from './ReadCommunity';
import Moment from 'react-moment'

const MoviesPage = () => {


  const getMovies = () => {
    const movies = [
      { id: 0, title: "기생충", genre: "블랙 코미디", release: "2019-05-30" },
      { id: 1, title: "라이온 킹", genre: "애니메이션", release: "2019-07-17" },
      { id: 2, title: "날씨의 아이", genre: "애니메이션", release: "2019-10-31" },
      { id: 3, title: "알라딘", genre: "판타지", release: "2019-05-23" },
      { id: 4, title: "나랏말싸미", genre: "역사", release: "2019-07-24" },
      { id: 5, title: "주전장", genre: "역사", release: "2019-07-25" },
      { id: 6, title: "어벤져스: 엔드게임", genre: "판타지", release: "2019-04-24" },
      { id: 7, title: "봉오동 전투", genre: "역사", release: "2019-08-07" },
      { id: 8, title: "김복동", genre: "역사", release: "2019-08-08" },
      { id: 9, title: "코코", genre: "애니메이션", release: "2018-01-11" },
      { id: 10, title: "코코", genre: "애니메이션", release: "2018-01-11" },
      { id: 11, title: "코코", genre: "애니메이션", release: "2018-01-11" },
      { id: 12, title: "코코", genre: "애니메이션", release: "2018-01-11" },
      { id: 13, title: "코코", genre: "애니메이션", release: "2018-01-11" },
      { id: 14, title: "코코", genre: "애니메이션", release: "2018-01-11" },

    ]
    return movies;
  }
  const [activityHistoryList, setActivityHistoryList] = useState({ // 영화 정보를 담는 state
    data: readActivityHistory(), // 영화 정보
    pageSize: 3, // 한 페이지에 보여줄 아이템(영화목록) 개수
    currentPage: 1 // 현재 활성화된 페이지 위치
  });

  function readActivityHistory() {
    axios
        .get('/happyhouse/communities')
        .then(({data}) => {
            data = data.communitysList
            setActivityHistoryList(data.map(communityList))

        })
}
useEffect(() => {
  readActivityHistory() //getlist
}, []);
  const [movies, setMovies] = useState({ // 영화 정보를 담는 state
    data: getMovies(), // 영화 정보
    pageSize: 3, // 한 페이지에 보여줄 아이템(영화목록) 개수
    currentPage: 1 // 현재 활성화된 페이지 위치
  });

  const handlePageChange = (page) => {
    setMovies({ ...movies, currentPage: page });
  };

  const { data, pageSize, currentPage } = movies;
  const pagedMovies = paginate(data, currentPage, pageSize); // 페이지 별로 아이템이 속한 배열을 얻어옴

  const { length: count } = movies.data;
  if (count === 0) 
    return <p>영화 정보가 없습니다.</p>;

    const communityList = (community) => (

      <li className="li" key={community._id} id={community._id}>

          <div class="community-block" id="second">
              <td className="id">{community._id}</td>
              <td className="id">{community.writeDate}</td>
              <td id="header" className={"readCommunity"}>
                  <h4>[{community.title}]</h4>
              </td>

              <div>
                  <table class="houseInfo">
                      <tr>
                          <td id="a">작성자</td>
                          <td>{community.userId}</td>
                          <td id="a">작성일</td>
                          <td>
                              <Moment format="YY.MM.DD">{community.writeDate}</Moment>
                          </td>
                      </tr>
                  </table>
              </div>

              <div class="community-content">
                  {community.content}
              </div>
              <br></br>
              <div className="button-container">
                  <button id="review-upload" className={"readCommunityReply"}>댓글</button>
                  {/* <button id="review-upload" className={"readCommunityDetail"}>수정/삭제</button> */}
              </div>
              <br></br>
          </div>
      </li>
  );

  return (
    <>
 
 <br></br>   <br></br>
 <br></br>   <br></br>
      <p>{count} 개의 영화 정보가 있습니다.</p>

      <table className="table">
        <thead>
          <tr>
            <th>ID</th>
            <th>Title</th>
            <th>Genre</th>
            <th>Release</th>
          </tr>
        </thead>
        <tbody>
          {pagedMovies.map((movie) => (
            <tr key={movie.id}>
              <td>{movie.id}</td>
              <td>{movie.title}</td>
              <td>{movie.genre}</td>
              <td>{movie.release}</td>
            </tr>
          ))}
        </tbody>
      
      </table>
      <Pagination
        pageSize={pageSize}
        itemsCount={count}
        currentPage={currentPage}
        onPageChange={handlePageChange}
      />
    </>
  );
};

export default MoviesPage;
