    }

    return (
        <div>
            <React.Fragment>

                <div className="review-wrap">
                    <div className="review-title">
                        <div id="title">거주 후기</div>
                    </div>

                    
                    <div class="review-container">
                        <div class="review-block">
                            <div id="header">
                                <h4>
                                            <Input
                                                name="title"
                                                onChange={handlChangeTitle}
                                                value={title}
                                            ></Input>
                                        </h4>
                            </div>
                          
                            <div id="writer-writeDate-star">
                                <div>{userId}
                                    /
                                    <Moment format="YY.MM.DD">{writeDate}</Moment>
                                        <Progress  value={star} max="5" />평점(0~5)</div><Input placeholder="평점(0~5)" name="star" onChange={handlChangeStar} value={star}></Input>
                                    
                            </div>

                            <div>
                                <table class="houseInfo">
                                    <tr>
                                        <td id="a">지역</td>
                                        <td id="a">
                                            <Input
                                                name="content"
                                                onChange={handlChangeRegion}
                                                value={region}
                                            ></Input>
                                        </td>

                                    </tr>
                                    <tr>
                                        <td id="a">임대종류</td>
                                        <td>
                                            <Input value="행복주택" readOnly={isReadOnly}></Input>
                                        </td>
                                        <td id="a">유형</td>
                                        <td>
                                            <Input value="아파트" readOnly={isReadOnly}></Input>
                                        </td>
                                    </tr>
                                    <tr>
                                        <td id="a">주택형</td>
                                        <td id="a">
                                            <Input
                                                name="content"
                                                onChange={handlChangeTypeName}
                                                value={typeName}
                                            ></Input>
                                        </td>

                                        <td id="a">공급수</td>
                                        <td>
                                            <Input value="32 세대" readOnly={isReadOnly}></Input>
                                        </td>
                                    </tr>
                                    <tr>
                                        <td id="a">월세</td>
                                        <td id="a">
                                            <Input
                                                name="monthlyRentCharge"
                                                onChange={handlChangeMonthlyRentCharge}
                                                value={numeral(monthlyRentCharge).format('0,0')}
                                            ></Input>
                                        </td>

                                        <td id="a">관리비</td>
                                        <td id="a">
                                            <Input
                                                name="adminCharge"
                                                onChange={handlChangeAdminCharge}
                                                value={numeral(adminCharge).format('0,0')}
                                            ></Input>
                                        </td>

                                    </tr>
                                </table>
                            </div>

                            <div class="review-image">
                                <img src={room1} id="reviewImage"></img>
                                {/* <div>{reviews.picture}</div> */}
                            </div>

                            <div class="review-content">
                                <div id="merit">
                                    <div id="b">장점</div>
                                    <Input
                                        name="merit"
                                        cols="50"
                                        rows="20"
                                        onChange={handlChangeMerit}
                                        value={merit}
                                        ></Input>

                                </div>
                                <div id="demerit">
                                    <div id="b">단점</div>
                                    <Input
                                        className="input-style"
                                        name="demerit"
                                        cols="50"
                                        rows="20"                                    
                                        onChange={handlChangeDemerit}
                                        value={demerit}
                                        ></Input>
                                </div>
                            </div>
                            <br></br>
                            <div className="button-container">
        <button id="review-upload" className={"createReviewButton"}>등록</button>
        <button id="review-upload" className={"readReviewDetail"}>취소</button>
        </div>
                        </div>
                    </div>
                </div>
            </React.Fragment>
        </div>

    )
}
export default ReadReview;
