import React, {useState, useEffect} from 'react';
import {
    Button,
    Progress,
    Table
} from 'reactstrap';
import axios from 'axios'
import Moment from 'react-moment'
import numeral from 'numeral'
import './Review.css'
import $ from 'jquery';
import room1 from'../Image/room1.PNG'
import room2 from'../Image/room2.PNG'
import {Modal, ModalHeader} from 'reactstrap';
import CreateReview from './CreateReview'
import ReadReview from './ReadReview';
const Review =(props)=>{
    useEffect(() => {
            readReview()
        }
    );
    const [modalInput, setModalInput] = useState("default");
    const [modalCreateReview, setModalCreateReview] = useState(false);
    const [modalReadReview, setModalReadReview] = useState(false);
    const toggleCreateReview = () => setModalCreateReview(!modalCreateReview);
    const toggleReadReview = () => setModalReadReview(!modalReadReview);
    const [review_list, setReview] = useState();
    const reviewList = (reviews) => (
        <li className="li" key={reviews._id} id={reviews._id}>
                    <div class="review-block">
                        <div id="header" className={"readCommunity"} onClick = {()=>{window.location.href ='/reviews/detail'}}>
                            <h4>{reviews.title}</h4>
                        </div>

                        <div id = "writer-writeDate-star">
                            <div>{reviews.userId} / <Moment format="YY.MM.DD">{reviews.writeDate}</Moment> <Progress value={reviews.star} max="5" /></div>
                        </div>

                        <div>
                            <table class="houseInfo">
                                <tr>
                                    <td id="a">지역</td>
                                    <td colspan="3">{reviews.region}</td>
                                </tr>
                                <tr>
                                    <td id="a">임대종류</td>
                                    <td>행복주택</td>
                                    <td id="a">유형</td>
                                    <td>아파트</td>
                                </tr>
                                <tr>
                                    <td id="a">주택형</td>
                                    <td>{reviews.typeName}</td>
                                    <td id="a">공급수</td>
                                    <td>32 세대</td>
                                </tr>
                                <tr>
                                    <td id="a">월세</td>
                                    <td>{numeral(reviews.monthlyRentCharge).format('0,0')}</td>
                                    <td id="a">관리비</td>
                                    <td>{numeral(reviews.adminCharge).format('0,0')}</td>
                                </tr>
                            </table>
                        </div>

                        <div class="review-image">
                             <img src={room1} id="reviewImage"></img> 
                            {/* <div>{reviews.picture}</div> */}
                        </div>

                        <div class="review-content">
                            <div id="merit"><div id="b">장점</div>{reviews.merit}</div>
                            <div id="demerit"><div id="b">단점</div>{reviews.demerit}</div>
                        </div>
                    </div>
        </li>
    );
    function readReview () {
        axios.get('/happyhouse/reviews').then(({data}) => {
            data = data.reviewList
            setReview(data.map(reviewList))
        })
    }
    $(function () {
        $(".readReview").off("click")
        $(".createReviewButton").on("click", function () {

            var Button = $(this);

            var div = Button.parent();
            var td = div.children();
            setModalInput(td.eq(0).text());
            localStorage.setItem("review_id",modalInput);
            toggleCreateReview();
        })
        $(".readReview").on("click", function () {

            var Button = $(this);
            var tr = Button.parent();
            var td = tr.children();
            setModalInput(td.eq(0).text());
            toggleReadReview();
        })
    })
