    return (
        <div>
        <React.Fragment>
            <div className="dv">

            <div className = "review-wrap">
                <div className = "review-title">
                    <div id = "title">거주 후기</div>
                </div>

                <div className = "search-button-group">
                    <select id = "review-search-option">
                        <option>최신순</option>
                    </select>
                    <input id="review-search" value=""></input>
                    <button id="review-upload" className={"createReviewButton"}>UPLOAD</button>
                </div>

                {/* <div class="review-container">
                    {review_list}
                </div> */}
                {review_list}
            </div>

            <div id="center">
                    <div class="pagination">
                        <a href="#">&laquo;</a>
                        <a href="#"  class="active">1</a>
                        <a href="#">2</a>
                        <a href="#">3</a>
                        <a href="#">4</a>
                        <a href="#">5</a>
                        <a href="#">&raquo;</a>
                    </div>
            </div>
        </div>

        </React.Fragment>

        <Modal isOpen={modalCreateReview}>
                <ModalHeader toggle={toggleCreateReview}>거주후기 작성</ModalHeader>
                <CreateReview></CreateReview>
            </Modal>

            <Modal isOpen={modalReadReview}>
                <ModalHeader toggle={toggleReadReview}>거주후기 상세조회</ModalHeader>
                <ReadReview _id={modalInput}></ReadReview>
            </Modal>
    </div>
    );
}

