import React, {Component} from 'react';
import {
    Button,
    Progress,
    Table
} from 'reactstrap';
import '../source/reviews.css'

class Reviews extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            reviews: [],
        };
    }

    componentDidMount() {
        fetch('http://localhost:8080/reviews')
            .then(abc => abc.json())
            .then(data => this.setState({reviews: data}));
    }
    render() {

        const {reviews} = this.state;
        const reviewsList = reviews.map((reviews) => (

           
            <tr key={reviews.id} id={reviews.id}>
               <td>{reviews.userId}</td>                          
               <td>{reviews.regon}</td>  
               <td>{reviews.houseType}</td>                  
               <td>{reviews.title}</td>
               <td>{reviews.writeDate}</td>
               <td> <Progress value={reviews.star} max="5" /></td>
            </tr>

        ));
        return (
            <div className="dv">
             
 <Button  color={"primary"}  style={{float: 'right'}} >{"거주후기 작성"}</Button>
                <Table class="tg" >

                    <thead>
                        <tr>
                            <th >작성자</th>                              
                            <th >주택지역</th>      
                            <th >주택유형</th>     
                            <th >제목</th>                    
                            <th >작성날짜</th>
                            <th >평점</th>
                        </tr>
                    </thead>
                    <tbody >                       
                        {reviewsList}
                    </tbody>
                </Table>
               
               
    
    
            </div>
        );
    }
}
export default Reviews;
